export default function Confirm(props){
    return(
        <div className="confirm-container">
            <img src={process.env.PUBLIC_URL + "/images/icon-success.svg"}  id="confirm-element"alt="" />
            <h1 className="title" id="confirm-element">Thanks for subscribing!</h1>
            <p id="confirm-element">A confirmation email has been sent to <span className="email-label">ash@loremcompany.com</span>.Please open it and click the button inside to confirm your subscription</p>
            <button className="button">Dismiss message</button>
        </div>
    )
}
